import React from 'react';

function TodoList({ tasks, onCheckboxChange, onDelete }) {
  return (
    <div>
      {tasks.map((task) => (
        <div key={task.id} className="card mb-3">
          <div className="card-body">
            <div className="d-flex justify-content-between align-items-center">
              <h5>{task.text}</h5>
              <div>
                <button className="btn btn-danger btn-sm" onClick={() => onDelete(task.id)}>
                  Delete
                </button>
              </div>
            </div>
            <p className="mb-1">Category: {task.category}</p>
            <p className="mb-1">Priority: {task.priority}</p>
            <p className="mb-1">Due Date: {task.dueDate}</p>

            <div>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => onCheckboxChange(task.id)}
              />
              <label className="ms-2">Mark as complete</label>
            </div>

            {task.subtasks && task.subtasks.length > 0 && (
              <div className="mt-3">
                <h6>Subtasks</h6>
                <ul className="list-group">
                  {task.subtasks.map((subtask) => (
                    <li key={subtask.id} className="list-group-item d-flex justify-content-between align-items-center">
                      <div>
                        <input
                          type="checkbox"
                          checked={subtask.completed}
                          onChange={() => onCheckboxChange(task.id, subtask.id)}
                        />
                        <span className={subtask.completed ? 'text-decoration-line-through ms-2' : 'ms-2'}>
                          {subtask.text}
                        </span>
                      </div>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
}

export default TodoList;
