import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect } from "react";
import TodoForm from './components/TodoForm/TodoForm';
import TodoList from './components/TodoList/TodoList';
import ContactCard from './components/ContactCard/ContactCard';

function App() {
  const [tasks, setTasks] = useState(() => {
    const savedTasks = JSON.parse(localStorage.getItem("tasks"));
    return savedTasks || [];
  });

  const [userName, setUserName] = useState(() => {
    return localStorage.getItem("userName") || "My To-Do List";
  });

  const [isEditingName, setIsEditingName] = useState(false);
  const [viewCount, setViewCount] = useState(0);

  // Save tasks to localStorage whenever tasks array changes
  useEffect(() => {
    if (tasks.length > 0) {
      localStorage.setItem("tasks", JSON.stringify(tasks));
    }
  }, [tasks]);

  // Save user name to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem("userName", userName);
  }, [userName]);

  // Initialize view count
  useEffect(() => {
    const storedViewCount = localStorage.getItem("viewCount");
    const newViewCount = storedViewCount ? parseInt(storedViewCount) + 1 : 1;
    localStorage.setItem("viewCount", newViewCount);
    setViewCount(newViewCount);
  }, []);

  const addTask = (taskText, taskCategory, priority, dueDate) => {
    const newTask = {
      id: Date.now(),
      text: taskText,
      date: new Date().toISOString(),
      category: taskCategory,
      priority,
      dueDate,
      completed: false,
    };
    setTasks([newTask, ...tasks]);
  };

  const handleCheckboxChange = (taskId) => {
    const updatedTasks = tasks.map((task) => {
      if (task.id === taskId) {
        task.completed = !task.completed;
      }
      return task;
    });
    setTasks(updatedTasks);
  };

  const handleDelete = (taskId) => {
    const updatedTasks = tasks.filter((task) => task.id !== taskId);
    setTasks(updatedTasks);
  };

  return (
    <div className="container mt-5">
      <h4>Total Views: {viewCount}</h4> {/* Display view count */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        {isEditingName ? (
          <form onSubmit={(e) => { e.preventDefault(); setIsEditingName(false); }} className="d-flex">
            <input
              type="text"
              className="form-control"
              value={userName}
              onChange={(e) => setUserName(e.target.value)}
            />
            <button className="btn btn-primary ms-2" type="submit">Save</button>
          </form>
        ) : (
          <>
            <h4>{userName}</h4>
            <button className="btn btn-secondary" onClick={() => setIsEditingName(true)}>Edit Name</button>
          </>
        )}
      </div>
      <TodoForm addTask={addTask} />
      <TodoList tasks={tasks} onCheckboxChange={handleCheckboxChange} onDelete={handleDelete} />
      <ContactCard />
    </div>
  );
}

export default App;
