import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect } from "react";
import TodoForm from './coTodoForm';
import TodoList from './TodoList';

function App() {
  const [tasks, setTasks] = useState([]);
  const [userName, setUserName] = useState("");
  const [isEditingName, setIsEditingName] = useState(false);

  useEffect(() => {
    const savedTasks = JSON.parse(localStorage.getItem("tasks")) || [];
    setTasks(savedTasks);
    const savedUserName = sessionStorage.getItem("userName") || "My To-Do List";
    setUserName(savedUserName);
  }, []);

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  useEffect(() => {
    sessionStorage.setItem("userName", userName);
  }, [userName]);

  const addTask = (taskText, taskCategory, priority) => {
    const newTask = {
      id: Date.now(),
      text: taskText,
      date: new Date().toISOString(),
      category: taskCategory,
      priority: priority,
      new: true,
      inProgress: false,
      completed: false,
    };
    setTasks([newTask, ...tasks]);
  };

  const handleCheckboxChange = (taskId) => {
    const updatedTasks = tasks.map((task) => {
      if (task.id === taskId) {
        task.completed = !task.completed;
        task.new = false;
        task.inProgress = false;
      }
      return task;
    });
    setTasks(updatedTasks);
  };

  const handleDelete = (taskId) => {
    const updatedTasks = tasks.filter((task) => task.id !== taskId);
    setTasks(updatedTasks);
  };

  const handleNameEdit = (e) => {
    e.preventDefault();
    setIsEditingName(false);
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        {isEditingName ? (
          <form onSubmit={handleNameEdit} className="d-flex">
            <input
              type="text"
              className="form-control"
              value={userName}
              onChange={(e) => setUserName(e.target.value)}
            />
            <button className="btn btn-primary ms-2" type="submit">
              Save
            </button>
          </form>
        ) : (
          <>
            <h4>{userName}</h4>
            <button
              className="btn btn-secondary"
              onClick={() => setIsEditingName(true)}
            >
              Edit Name
            </button>
          </>
        )}
      </div>
      <div className="form-body mb-4">
        <TodoForm addTask={addTask} />
      </div>
      <TodoList
        tasks={tasks}
        onCheckboxChange={handleCheckboxChange}
        onDelete={handleDelete}
      />
    </div>
  );
}

export default App;
